div.script:
    document.addEventListener("DOMContentLoaded", function () {
        if (document.body.classList.contains("editor_started")) return;
        (function () {
  const oldSections = document.querySelectorAll('.s_custom_anchorArea');
  if (oldSections.length === 0) return;
  const firstSection = oldSections[0];
  const targetRow = firstSection.querySelector('.row');
  if (!targetRow) return;
  // 預先蒐集所有 col 元素
  const colElements = [];
  oldSections.forEach((section, sectionIndex) => {
    const id = section.getAttribute('id');
    const isAnchor = section.getAttribute('data-anchor') === 'true';
    const cols = section.querySelectorAll('[class*="col-lg"]');
    cols.forEach((col, colIndex) => {
      const clone = col.cloneNode(true);
      // 將 id 和 data-anchor 僅放在每區塊的第一個 col 上
      if (colIndex === 0 && isAnchor && id) {
        clone.setAttribute('id', id);
        clone.setAttribute('data-anchor', 'true');
      }
      colElements.push(clone);
    });
  });
  // 清空第一個 section 的 row
  targetRow.innerHTML = '';
  // 將整理好的 col 加回第一個 row
  colElements.forEach(col => targetRow.appendChild(col));
  // 清除第一個 section 的 id 和 data-anchor 屬性
  firstSection.removeAttribute('id');
  firstSection.removeAttribute('data-anchor');
  // 移除其他多餘的 section
  oldSections.forEach((section, i) => {
    if (i !== 0) section.remove();
  });
})();
    });

