div.script:
document.addEventListener('DOMContentLoaded', function () {
  const isLargeScreen = window.innerWidth > 991;
  // 監聽 body class 變化，編輯模式時移除動態內容
  const observer = new MutationObserver(function (mutations) {
    mutations.forEach(function (mutation) {
      if (mutation.type === 'attributes' && mutation.target.classList.contains('editor_started')) {
        document.querySelectorAll('.cus-wrap').forEach(wrap => wrap.remove());
      }
    });
  });
  observer.observe(document.body, {
    attributes: true,
    attributeFilter: ['class'],
  });
  // 如果不是編輯模式且是大螢幕，執行主邏輯
  if (!document.body.classList.contains('editor_started') && isLargeScreen) {
    const sections = document.querySelectorAll('.s_custom_fullHoverBackground');
    sections.forEach(sectionBlock => {
      // 跳過有 s_custom_hidden 的
      if (sectionBlock.classList.contains('s_custom_hidden')) return;
      const section = sectionBlock.querySelector('.s_product_product_horizontal');
      if (!section) return;
      // 避免重複插入
      if (section.querySelector('.cus-wrap')) return;
      let bg1 = sectionBlock.querySelector('.bg-image.bg1');
      let bg2 = sectionBlock.querySelector('.bg-image.bg2');
      if (!bg1 || !bg2) {
        bg1 = document.createElement('div');
        bg2 = document.createElement('div');
        bg1.className = 'bg-image bg1 active';
        bg2.className = 'bg-image bg2';
        sectionBlock.appendChild(bg1);
        sectionBlock.appendChild(bg2);
      }
      const switchBackground = function (url) {
        const current = sectionBlock.querySelector('.bg-image.active');
        const next = current === bg1 ? bg2 : bg1;
        next.style.backgroundImage = `url('${url}')`;
        next.classList.add('active');
        current.classList.remove('active');
      };
      // 建立自定義資訊與按鈕結構
      const wrap = document.createElement('div');
      wrap.className = 'cus-wrap';
      const infoWrap = document.createElement('div');
      infoWrap.className = 'cus-info-wrap';
      const btnWrap = document.createElement('div');
      btnWrap.className = 'cus-btn-wrap';
      const cards = section.querySelectorAll('.o_carousel_product_card');
      cards.forEach((card, index) => {
        const info = card.querySelector('.o_carousel_product_card_body .info-wrap');
        const btn = card.querySelector('.d-flex.justify-content-between');
        const infoList = document.createElement('div');
        infoList.className = 'cus-info-list';
        if (info) infoList.appendChild(info.cloneNode(true));
        infoWrap.appendChild(infoList);
        const btnList = document.createElement('div');
        btnList.className = 'cus-btn-list';
        if (btn) {
          const btnIcon = btn.querySelector('.cus-icon-btn');
          if (btnIcon) btnList.appendChild(btnIcon.cloneNode(true));
        }
        btnList.dataset.index = index;
        btnWrap.appendChild(btnList);
      });
      wrap.appendChild(infoWrap);
      wrap.appendChild(btnWrap);
      section.appendChild(wrap);
      // 預設顯示第一個
      const btnLists = wrap.querySelectorAll('.cus-btn-list');
      const infoLists = wrap.querySelectorAll('.cus-info-list');
      if (btnLists[0]) btnLists[0].classList.add('active');
      if (infoLists[0]) infoLists[0].classList.add('show');
      const defaultImg = cards[0]?.querySelector('.position-static img');
      if (defaultImg?.src) switchBackground(defaultImg.src);
      // 滑入切換
      btnLists.forEach((btn, idx) => {
        btn.addEventListener('mouseenter', () => {
          btnLists.forEach(b => b.classList.remove('active'));
          infoLists.forEach(i => i.classList.remove('show'));
          btn.classList.add('active');
          if (infoLists[idx]) infoLists[idx].classList.add('show');
          const img = cards[idx]?.querySelector('.position-static img');
          if (img?.src) switchBackground(img.src);
        });
      });
      // 額外插入 .s_custom_allCatBtn
      const allCatBlock = sectionBlock.querySelector('.s_custom_allCatBtn');
      if (allCatBlock) {
        const allBtns = allCatBlock.querySelectorAll('a.btn');
        if (allBtns.length > 0) {
          const allBtnWrap = document.createElement('div');
          allBtnWrap.className = 'cus-all-btn-wrap';
          allBtns.forEach(btn => allBtnWrap.appendChild(btn.cloneNode(true)));
          btnWrap.insertAdjacentElement('afterend', allBtnWrap);
        }
      }
    });
  }
});

