div.script:
document.addEventListener("DOMContentLoaded", function () {
    function debounce(fn, delay) {
        let timer = null;
        return function (...args) {
            clearTimeout(timer);
            timer = setTimeout(() => fn.apply(this, args), delay);
        };
    }
    function smoothScrollBy(container, distance, duration = 400) {
        const start = container.scrollLeft;
        const startTime = performance.now();
        function scrollStep(timestamp) {
            const elapsed = timestamp - startTime;
            const progress = Math.min(elapsed / duration, 1);
            const ease = 0.5 - Math.cos(progress * Math.PI) / 2;
            container.scrollLeft = start + distance * ease;
            if (progress < 1) requestAnimationFrame(scrollStep);
        }
        requestAnimationFrame(scrollStep);
    }
    function getScrollDistance() {
        let raw = getComputedStyle(document.documentElement)
            .getPropertyValue('--scroll-distance2').trim();
        if (raw.endsWith('px')) {
            return parseFloat(raw);
        } else if (raw.endsWith('%')) {
            return window.innerWidth * (parseFloat(raw) / 100);
        } else {
            // 如果沒有單位，直接當成 px
            return parseFloat(raw);
        }
    }
    let isScrollingLocked = false;
    const scrollCooldown = 800;
    function initAccordions() {
        const accordions = document.querySelectorAll(".s_custom_worldwide:not(.s_custom_hidden) .accordion");
        accordions.forEach(accordion => {
            accordion.style.overflowX = "auto";
            accordion.style.overflowY = "hidden";
            accordion.style.overscrollBehaviorX = "contain";
            const isScrollable = accordion.scrollWidth > accordion.clientWidth;
            let leftArrow = accordion.parentElement.querySelector('.carousel-arrow-left');
            let rightArrow = accordion.parentElement.querySelector('.carousel-arrow-right');
            if (!isScrollable) {
                if (leftArrow) leftArrow.remove();
                if (rightArrow) rightArrow.remove();
                return;
            }
            if (!leftArrow) {
                leftArrow = document.createElement("div");
                leftArrow.classList.add("carousel-arrow", "carousel-arrow-left");
                leftArrow.innerHTML = '<i class="fa fa-angle-left" aria-hidden="true"></i>';
                accordion.insertAdjacentElement("afterend", leftArrow);
            }
            if (!rightArrow) {
                rightArrow = document.createElement("div");
                rightArrow.classList.add("carousel-arrow", "carousel-arrow-right");
                rightArrow.innerHTML = '<i class="fa fa-angle-right" aria-hidden="true"></i>';
                leftArrow.insertAdjacentElement("afterend", rightArrow);
            }
            accordion.addEventListener("scroll", function () {
                const maxScrollLeft = accordion.scrollWidth - accordion.clientWidth;
                leftArrow.classList.toggle("disabled", accordion.scrollLeft <= 0);
                rightArrow.classList.toggle("disabled", accordion.scrollLeft >= maxScrollLeft);
            });
            accordion.addEventListener("wheel", function (e) {
                const maxScrollLeft = accordion.scrollWidth - accordion.clientWidth;
                const atLeftEdge = accordion.scrollLeft <= 0;
                const atRightEdge = accordion.scrollLeft >= maxScrollLeft;
                if ((atLeftEdge && e.deltaY < 0) || (atRightEdge && e.deltaY > 0)) {
                    if (isScrollingLocked) return;
                    e.preventDefault();
                    isScrollingLocked = true;
                    setTimeout(() => isScrollingLocked = false, scrollCooldown);
                } else {
                    e.preventDefault();
                    smoothScrollBy(accordion, e.deltaY * 2, 400);
                }
            }, { passive: false });
            leftArrow.addEventListener("click", function () {
                smoothScrollBy(accordion, -getScrollDistance(), 400);
            });
            rightArrow.addEventListener("click", function () {
                smoothScrollBy(accordion, getScrollDistance(), 400);
            });
        });
    }
    function removeArrows() {
        document.querySelectorAll('.carousel-arrow-left, .carousel-arrow-right').forEach(el => el.remove());
    }
    const observer = new MutationObserver(function (mutations) {
        mutations.forEach(function (mutation) {
            if (mutation.type === 'attributes' && mutation.attributeName === 'class') {
                if (document.body.classList.contains('editor_started')) {
                    removeArrows();
                } else {
                    initAccordions();
                }
            }
        });
    });
    observer.observe(document.body, { attributes: true });
    if (!document.body.classList.contains("editor_started")) {
        initAccordions();
    }
});

