        mutations.forEach(function (mutation) {
            if (mutation.type === 'attributes' && mutation.attributeName === 'class') {
                if (document.body.classList.contains('editor_started')) {
                    removeArrows();
                } else {
                    initAccordions();
                }
            }
        });
    });
    observer.observe(document.body, { attributes: true });
    if (!document.body.classList.contains("editor_started")) {
        initAccordions();
    }
});

document.addEventListener("DOMContentLoaded", function () {
    document.querySelectorAll(".copy-btn").forEach(button => {
        button.addEventListener("click", function () {
            let parentSection = this.closest("section");
            let targetSection = parentSection?.nextElementSibling;
            if (targetSection && targetSection.tagName.toLowerCase() === "section") {
                // **克隆 targetSection**
                let clonedSection = targetSection.cloneNode(true);
                // **移除 .dynamic_snippet_template 內的內容**
                clonedSection.querySelectorAll(".dynamic_snippet_template").forEach(el => {
                    while (el.firstChild) {
                        el.removeChild(el.firstChild); // 清空所有子元素
                    }
                });
                // **使用 XMLSerializer 轉換為 XML 格式**
                let serializer = new XMLSerializer();
                let xmlContent = serializer.serializeToString(clonedSection);
                // **確保 XML 格式正確**
                xmlContent = xmlContent.replace(/xmlns="http:\/\/www.w3.org\/1999\/xhtml"/g, ""); // 移除 xmlns
                xmlContent = xmlContent.replace(/<br>/g, "<br/>"); // 修正 <br> 為自閉合標籤
                xmlContent = xmlContent.replace(/<img([^>]*?)\/{2,}>/g, "<img$1/>"); // 修正 <img>，確保只有一個 /
                // **創建隱藏的 textarea 來存放 XML 並複製**
                let textarea = document.createElement("textarea");
                textarea.value = xmlContent;
                document.body.appendChild(textarea);
                textarea.select();
                document.execCommand("copy");
                document.body.removeChild(textarea);
                alert("已成功複製 XML 格式的 HTML 區塊！");
            } else {
                alert("找不到可複製的區塊！");
            }
        });
    });
    // 新增複製 SCSS 的功能
  document.querySelectorAll(".copy-scss-btn").forEach(button => {
    button.addEventListener("click", function () {
        let parentSection = this.closest("section"); // 找到按鈕所在的 section
        let scssElement = parentSection?.querySelector("#scss-code"); // 在該 section 內尋找 scss 代碼
        if (scssElement) {
            let scssContent = scssElement.textContent;
            let textarea = document.createElement("textarea");
            textarea.value = scssContent;
            document.body.appendChild(textarea);
            textarea.select();
            document.execCommand("copy");
            document.body.removeChild(textarea);
            alert("已成功複製 SCSS 代碼！");
        } else {
            alert("找不到 SCSS 代碼區塊！");
        }
    });
});
});
